import React, { useState, useEffect, useRef } from "react";
import Selector from './components/Selector'
import './style.css';
var Latex = require('react-latex');
//import './css/main.css';
// import './custom.css';
// import './minty.css';
// import './zephyr.css';

// change it to cyclops.cse.iitk.ac.in
//const SERVER="http://cyclops.cse.iitk.ac.in:5000/"
 const SERVER="http://localhost:5000/"

async function contactServer(endpoint, query) {
    var fetchlink
    if (query == null)
        fetchlink = SERVER + endpoint
    else
        fetchlink = SERVER + endpoint + "?" + query
    return fetch(fetchlink, {method : "GET"})
.then(response => response.json())
.then(data => {console.log("Sending request for " + fetchlink + " \n"); return data;})
.catch(console.log("Got error\n"));
}

//const passcode = React.createRef();
function Solve() {
    const [passcode, setPasscode] = useState("");
    const [grammar, setGrammar] = useState("");
    const [task, setTask] = useState("");
    const [grammarlist, setGrammarList] = useState("");
    const [tasklist, setTaskList] = useState("");
    const [parsetable, setParseTable] = useState("");
    const [solution, setSolution] = useState("");
    const [feedback, setFeedback] = useState("");
    const [finalmsg, setFinalMsg] = useState("");

    // input: JOSN containing Grammar, Feedback from cyclops and the parse table
    // returns: blocks of <div> displaying Grammar, parse table and feedback (in the given order)
    function Display() {
        if(parsetable === ""){
            return ( <div> </div>)
        }
        else{
        var jsonD = JSON.parse(parsetable)
        const grammarDiv = jsonD["Grammar"].map((ele) =>
                <li>{ele}</li>
            )
        const feedbackDiv = jsonD["Feedback"].map((ele) =>
                <li>{ele}</li>
            )

        //{'non_term' : 'A',"a" : 0,"c" : 3,"b" : 3,"$" : 0}
        var ptKeys = Object.keys(jsonD["ParseTable"][0]).map((ele) =>
                    ele
            )

        var ptHead = ["non_term"];
        var ptHeadD = ["Non-Term"]
        for(let i =0; i < ptKeys.length; i++){
            if( "non_term" !== ptKeys[i]){
                ptHead.push(ptKeys[i])
                ptHeadD.push(ptKeys[i])
            }
        }

        const ptHeadDiv = ptHeadD.map((ele) =>
                <th className="border border-slate-400 border-black text-lg" style={{backgroundColor: "darkslategrey", color: "white"}}>{ele}</th>
        )

        const ptBodyDiv = jsonD["ParseTable"].map((ele) =>
            <tr className="border-4 border-slate-100 border-black text-lg">
            {ptHead.map((k) => <td className="border border-slate-100 border-black"> {ele[k]} </td> )}
            </tr>
        )
        return (

                <div className="flex flex-col container grid grid-cols-1 gap-2">
                {/* Sumit : Edited this */}
                <div className="font-mono flex flex-col justify-center gap-2 text-bold border-2 bg-gray-200 col-span-1
                  border-blue-500 px-5 py-5   p-6 rounded-lg shadow-lg">
                    <div className="font-mono flex flex-row justify-center gap-2">
                    <div className="bg-yellow-100 border-2 border-yellow-500 px-3 py-3 rounded-lg shadow-lg">
                     <h6 class="font-bold text-sm text-xl font-medium leading-normal mt-0 mb-2 text-blue-900 bg-red-200">
                      Grammar Provided:
                        </h6>
                      <ol className="text-lg">{grammarDiv}</ol>
                    </div>
                    <br />
                    <div className="px-3 py-3 bg-green-100 col-span-2
                      border-green-500 border-2 rounded-lg shadow-lg items-start justify-start">
                            <h6 className="font-bold font-medium text-xl leading-normal mt-0 mb-2 text-blue-900 bg-red-200">
                                Parse Table :
                            </h6>
                            <table border="1" className="table table-auto table-striped border-black border border-slate-400 ">
                                <thead>
                                {ptHeadDiv}
                                {ptBodyDiv}
                                </thead>
                            </table>
                    </div>
                    </div>
                    <div className="font-bold text-red-900"><span className="font-mono">'eps'</span> represents <Latex>$\epsilon$ </Latex></div>
                </div>
